import React from 'react';
import {TextField, Button} from '@material-ui/core';


class HWSet extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            name: 'HWSet' + this.props.number,
            capacity: 100,
            available: 100,
            projectId: Number(this.props.projectId),
            input: 0,
            message: '',
        }
    }

    handleChange=(e)=>{
        this.setState({input: e.target.value});
    }

    render(){
        let title = this.state.name + ': ' + this.state.available + '/' + this.state.capacity;
        return(
            <>
            <div>
                <h2>{title}</h2>
            </div>

            <div>
                <TextField 
                    id="outlined-basic"
                    label="Enter qty"
                    onChange={this.handleChange}
                />
            </div>
            <Button
                onClick={() =>{
                    this.setState({available: Number(this.state.available) + Number(this.state.input)});
                    fetch('/checkIn/'+this.state.projectId+'/'+this.state.input).then((response) => {
                        return response.json();
                    }).then((myJson) => {
                        this.setState({message: myJson.message});
                    })  
                }}
                size={'medium'}
                > Check In </Button>
            <Button
                onClick={() =>{
                    this.setState({available: this.state.available - this.state.input});
                    fetch('/checkOut/'+this.state.projectId+'/'+this.state.input).then((response) => {
                        return response.json();
                    }).then((myJson) => {
                        this.setState({message: myJson.message});
                    })  
                }}
                size={'medium'}
            > Check Out </Button>
            <p>
                {this.state.message}
            </p>
            </>
            
        );
    }

}

class Project extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            hwSets: Array(2).fill(null),
            name: 'Project Name ' + this.props.number,
            projectID: Number(this.props.number),
            users: this.props.users,
            isjoined: this.props.isjoined,
            message: '',
        };
    }

    renderHWSet(num, projectId){
        return(
            <HWSet
                number={num}
                projectId={projectId}
            />
        );
    }

    render(){
        let title = this.state.name;
        let users = this.state.users;
        let projectID = this.state.projectID;
        let joined;
        if(this.state.isjoined)
            joined = 'Leave';
        else
            joined = 'Join';
        return(
            <>
            <div>
                <h1>{title}</h1>
                <h2>{users}</h2>
            </div>
            <div>
                {this.renderHWSet(1, projectID)}
                {this.renderHWSet(2, projectID)}
            </div>
            <div>
                <Button
                    onClick={() =>{
                        this.setState({isjoined: !this.state.isjoined})
                        if(!this.state.isjoined){
                            fetch('/join/'+this.state.projectId).then((response) => {
                                return response.json();
                            }).then((myJson) => {
                                this.setState({message: myJson.message});
                            }) 
                        }else{
                            fetch('/leave/'+this.state.projectId).then((response) => {
                                return response.json();
                            }).then((myJson) => {
                                this.setState({message: myJson.message});
                            }) 
                        }
                    }}
                    size={'medium'}
                > {joined} </Button>
            </div>
            <p>
                {this.state.message}
            </p>
            </>
        );
    }

}


export class Projects extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            projects: Array(3).fill(null), 
        };
    }

    renderProject(num){
        return(
            <Project
                number={num}
                users={'list of authorized users'}
                isjoined={false}
            />
        );
    }

    render(){

        return(
            <>
            <div>
                <h1>Projects</h1>
            </div>
            <div>
                {this.renderProject(1)}
                {this.renderProject(2)}
                {this.renderProject(3)}
            </div>
            </>
        );
    }
}
