import React from 'react';
import {TextField} from 'react';

class hwSet extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            name: 'HWSet' + this.props.number,
            capacity: 100,
            available: 100,
            input: 0,
        }
    }

    handleChange=(e)=>{
        this.setState({input: e.target.value});
    }

    render(){
        let title = this.state.name + ': ' + this.state.available + '/' + this.state.capacity;
        return(
            <>
            <div>
                <h2>{title}</h2>
            </div>

            <div>
                <TextField 
                    id="outlined-basic"
                    label="Enter qty"
                    onChange={this.handleChange}
                />
            </div>
            <button
                label="Check In"
                onClick={() =>{
                    this.setState({available: this.state.available + this.state.input});
                }}
            />
            <button
                label="Check Out"
                onClick={() =>{
                    this.setState({available: this.state.available - this.state.input});
                }}
            />
            </>
        );
    }

}

class project extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            hwSets: Array(2).fill(null),
            name: 'Project Name ' + this.props.number,
            users: this.props.users,
            isJoined: this.props.isJoined,
        };
    }

    renderHWSet(num){
        return(
            <hwSet
                number={num}
            />
        );
    }

    render(){
        let title = this.state.name;
        let users = this.state.users;
        let joined;
        if(this.state.isJoined)
            joined = 'Leave';
        else
            joined = 'Join';
        return(
            <>
            <div>
                <h1>{title}</h1>
                <h2>{users}</h2>
            </div>
            <div>
                {this.renderHWSet(1)}
                {this.renderHWSet(2)}
            </div>
            <div>
                <button
                    label={joined}
                    onClick={() =>{
                        this.setState({isJoined: !this.state.isJoined})
                    }}
                />
            </div>
            </>
        );
    }

}


class Projects extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            projects: Array(3).fill(null), 
        };
    }

    renderProject(num){
        return(
            <project
                number={num}
                users={'list of authorized users'}
                isJoined={false}
            />
        );
    }

    render(){

        return(
            <>
            <div>
                <h1>Projects</h1>
            </div>
            <div>
                {this.renderProject(1)}
                {this.renderProject(2)}
                {this.renderProject(3)}
            </div>
            </>
        );
    }
}
